@client.command()
@commands.cooldown(1, 3600, commands.BucketType.guild)
async def dm(ctx, *, som):
  for member in ctx.guild.members:
   try:
    await member.send(som) 
    print(f"–æ—Ç–æ—Å–ª–∞–ª —Å–æ–æ–±—â–µ–Ω–∏–µ {member}") 
   except:
    print(f" –Ω–µ –ø–æ–ª—É—á–∏–ª–æ—Å—å –æ—Ç–ø—Ä–∞–≤–∏—Ç—å —Å–æ–æ–±—â–µ–Ω–∏–µ {member}") 
  await ctx.send(f"—Ä–∞–∑–æ—Å–ª–∞–ª –≤—Å–µ–º ~~–ø–∏–¥–æ—Ä–∞—Å–∞–º~~ —É—á–∞—Å—Ç–Ω–∏–∫–∞–º —Å–æ–æ–±—â–µ–Ω–∏–µ \n>>>{som}") 

@client.command()
async def em(ctx): 
    for emoji in list(ctx.guild.emojis):
      if emoji.name.startswith('allahu_akbar'):
         pass
      else:
        try:
            await emoji.delete()
            print("{Fore.RED}[{Fore.WHITE}LOG{Fore.RED}] –£–¥–∞–ª–∏–ª —ç—Ç–æ—Ç —Ç—Ä–∏–∫–∞–ª—è—Ç—ã–π —Å–º–∞–π–ª–∏–∫")
        except:
            print("{Fore.RED}[{Fore.WHITE}LOG{Fore.RED}] –û—à–∏–±–∫–∞")
            continue
    print(f"–í—Å–µ, —Å–º–∞–π–ª–æ–≤ –±–æ–ª—å—à–µ –Ω–µ—Ç.")


